import { useEffect, useState } from 'react';
import { Data } from '../Data';
import Axios from "axios"
import BarChart from '../components/Bar';
import LineChart from '../components/LineChart';

export const Vertical = () => {
  const [userData, setUserData] = useState({

    labels: [],
    datasets: [
      {
        label: "Users Gained",
        data: [],
        backgroundColor: ['red', "aqua"],
        borderColor: "black",
        borderWidth: 2,
      },
      // {
      //   label:  "Users userLost", 
      //   data: UserData.map((data)=> data.userLost ),
      //   backgroundColor: ['green'], 
      //   borderColor: "black", 
      //   borderWidth : 2,
      // },

    ],
  })
  const [UserData2, setUserData2] = useState([])
  // let bla = UserData2.foo
  // console.log(bla, "c")

  useEffect(() => {
    Axios.get('http://localhost:3001/').then((response) => {
      setUserData(response.data)




      const IDs = response.data.map((data1) => data1.date);


      const foo = IDs.reduce((preval, val) => {
        const age = new Date().getFullYear() - val
        if (age >= 0 && age < 11) {
          preval['0-10'].push(val)
        }else if(age > 10 && age < 21){
          preval['11-20'].push(val)
        }else if (age > 20 && age < 31){
          preval['21-30'].push(val)
        }else if (age > 30 && age < 41){
        preval['31-40'].push(val)
        }else if (age > 40 && age < 51){
          preval['41-50'].push(val)
         }else if (age > 50 && age < 61){
          preval['51-60'].push(val)
        }else if (age > 60 && age < 71){
        preval['61-70'].push(val)
      }else {
        preval['+71'].push(val)
      }
          
        
          
        

        return preval
      }, { '0-10': [], '11-20': [], "21-30": [], "31-40": [], "41-50": [], "51-60": [], "61-70": [], "+71": [] })

      console.log(response.data);

      console.log(foo);

      const IDs1 = response.data.map((data1) => data1.count);
      // console.log(IDs);
      const dataset = {
        label: "Users id",
        data: IDs1,
        backgroundColor: ['red', "aqua", "Beige", "Blue", "Brown", "Chartreuse", "Coral", "DarkGrey", "DarkOrchid", "DeepPink"],
        borderColor: "black",
        borderWidth: 2,
      }





      // const foo = IDs.reduce((preval, val )=>{
      //   if(val> 50 && val <55) 
      //   return preval + 1
      //   else{
      //     return preval
      //   }
      //     },0)
      setUserData(prevUserData => ({ ...prevUserData, labels: IDs1 }))
      setUserData(prevUserData => ({ ...prevUserData, datasets: [dataset] }))
      setUserData2(prevUserData => ({ ...prevUserData, foo: foo }))
    })
  }, [])


  //   const [id , setId]= useState({

  //  })
  // Axios.get("http://localhost:3001/fetchbyid/:{id}").then((response) => {
  //   setUserData(response.data)
  // })


  return (
    <div style={{ width: 700 }}>
      <BarChart chartData={userData} />
      {/* <div >{bla} מספר האיי די מתחת ל כולל 55  </div>
      <select>
  <option value="18-30">18-30</option>
  <option value="31-40">31-40</option>
  <option value="41-50">41-50</option>
  <option value="51-60">51-60</option>
  <option value="61-70">61-70</option>
  <option value="71-100">61-70</option>
      </select> */}
    </div>

  )
}


export default Vertical
